wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(24668328126036949)
,p_process_sequence=>50
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'CLOSE_DISPUTES'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE ',
'',
'cursor c1 ( AP NUMBER, P_VENDOR VARCHAR2)  ',
'is',
'SELECT   DISPUTE_ID, ',
'         LOAN_NUMBER,',
'         CATEGORY,',
'         SUBCATEGORY, ',
'         CYDS,',
'         SGRESPONSE,',
'         AMOUNTREFUNDED,',
'         LOSSREASON,',
'         UPDATERERROR,',
'         RESPONSIBLEEMPLOYEESPILOGIN,',
'         COMMENTSTOVENDOR,',
'         INTERNALCOMMENTS',
'from corp_vendor_disputes ',
'WHERE activeprocess = AP',
'AND   VENDOR = P_VENDOR',
'AND   PROCESSOR IS NULL;',
'',
'R1      C1%ROWTYPE;',
'CNT     NUMBER;',
'TOT_CNT NUMBER;',
'',
'BEGIN',
'    CNT     := 0;                ',
'    TOT_CNT := 0;',
'',
'   INSERT INTO BOA_PROCESS_LOG (PROCESS,',
'                                SUB_PROCESS,',
'                                ENTRYDTE,',
'                                ROWCOUNTS,',
'                                MESSAGE)',
'        VALUES (''Corp_Vendor_Disputes'',',
'                ''Pg # 78'',',
'                  SYSDATE,',
'                 :P78_ACTIVEPROCESS,',
'                 :P78_TRAN_KEY);',
'',
'   COMMIT;',
'    OPEN C1(:P78_ACTIVEPROCESS, :P78_VENDOR);      ',
'',
'    LOOP',
'       FETCH C1 INTO R1;',
'       EXIT WHEN C1%NOTFOUND;',
'',
'           UPDATE corp_vendor_disputes ',
'              SET LOAN_NUMBER =  :P78_LOAN_NUMBER,',
'              CATEGORY        = :P78_CATEGORY,',
'              SUBCATEGORY     = :P78_SUBCATEGORY,',
'              CYDS            = :P78_CYDS,',
'              SGRESPONSE      = :P78_SGRESPONSE, ',
'              AMOUNTREFUNDED  = :P78_AMOUNTREFUNDED,',
'              LOSSREASON      = :P78_LOSSREASON,',
'              UPDATERERROR     = :P78_UPDATERERROR,',
'              RESPONSIBLEEMPLOYEESPILOGIN = :P78_RESPONSIBLEEMPLOYEESPILOGI,',
'              COMMENTSTOVENDOR =  :P78_COMMENTSTOVENDOR,',
'              INTERNALCOMMENTS =  :P78_INTERNALCOMMENTS,',
'              DATEOFSGRESPONSE = SYSDATE, ',
'              PROCESSOR = UPPER(:P0_APP_USER), ',
'              ESCALATED = ( case when :P78_SGRESPONSE = ''Escalate'' THEN 1 ELSE 0 END),',
'              HOWLONGYOUTOOK = (SYSDATE  - DATEDISPUTERECEIVED )                   ',
'        WHERE DISPUTE_ID  = R1.DISPUTE_ID; ',
'',
'         UPDATE CORP_VENDOR_DISPUTE_DETAILS',
'               set END_TIME = SYSDATE, ',
'                   SPI_USER_DEPT = :P78_SPI_USER_DEPT,',
'                   TRAN_KEY      = :P78_TRAN_KEY',
'                WHERE DISPUTE_ID = R1.DISPUTE_ID;',
'        ',
'        CNT := SQL%ROWCOUNT;',
'        TOT_CNT := TOT_CNT + CNT;         ',
'',
'    END LOOP;',
'                            ',
'    CLOSE C1;   ',
'    ',
'    COMMIT;',
'',
'   INSERT INTO BOA_PROCESS_LOG (PROCESS,',
'                                SUB_PROCESS,',
'                                ENTRYDTE,',
'                                ROWCOUNTS,',
'                                MESSAGE)',
'        VALUES (''Corp_Vendor_Disputes'',',
'                ''Pg # 78'',',
'                SYSDATE,',
'                 TOT_CNT,',
'                ''Mass update page'');',
'',
'   COMMIT;',
'',
'    ',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
