,p_process_name=>'Update row forword'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE ',
'',
'USERS_ID         NUMBER;  ',
'NEXT_STEP        VARCHAR2(30 BYTE);',
'CHARGE_BACK_AMT  VARCHAR2(20 BYTE);',
'CHARGE_BACK_NBR  NUMBER;',
'CLOSED           NUMBER;',
'DISPUTE_NO       NUMBER;',
'SAVED_WO         VARCHAR2(20 BYTE);',
'',
'BEGIN',
'',
' SELECT USER_ID ',
'   INTO USERS_ID ',
'   FROM LA_EMP_SECURITY ',
'   WHERE UPPER(LOGIN) = UPPER(:P175_USERNAME);',
'',
'    BEGIN',
'        SELECT WRITE_OFF ',
'        INTO SAVED_WO   ',
'        FROM LA_ASSIGN_DETAIL',
'        WHERE DISPUTE_ID = :P45_DISPUTE_ID',
'        AND ROWNUM = 1;',
'    EXCEPTION WHEN OTHERS THEN ',
'        SAVED_WO := ''No'';',
'    END;',
'',
'NEXT_STEP := CASE WHEN :P45_APPROVAL IN (''Pending Approval'')  then ''Invoice review''',
'                  WHEN :P45_APPROVAL IN (''Not Approved'')      then ''Dispute Closed''',
'                  WHEN :P45_PENDING_RESEARCH IN (''Yes'') then ''PENDING RESEARCH''',
'                  WHEN :P45_APPEAL IN (''Yes'') then ''APPEAL Submit''',
'             END;     ',
'',
'CLOSED    := CASE WHEN :P45_APPROVAL IN (''Not Approved'')  then USERS_ID ELSE 0 END;',
'',
'',
'',
'UPDATE LA_ASSIGNMENT',
'  SET CLIENT_CODE       = :P45_CLIENT_CODE,',
'      WRITE_OFF         =  SAVED_WO,',
'      VENDOR_CODE       = :P45_VENDOR_CODE,',
'      CHARGE_BACK_AMT_1 = :P45_CHARGE_BACK_AMT_1,',
'      APPROVAL          = :P45_APPROVAL,',
'      PENDING_RESEARCH  = :P45_PENDING_RESEARCH,',
'      REVIEWER_EMAIL_ADDRESS = :P45_REVIEWER_EMAIL_ADDRESS,',
'      APPEAL            = :P45_APPEAL,',
'      DISPUTE_APPEAL_COMMENT = :P45_DISPUTE_APPEAL_COMMENT,',
'      DEPARTMENT        = :P45_DEPARTMENT,',
'      CLIENT_EMPL       = :P45_CLIENT_EMPL,',
'      BUCKET            = :P45_BUCKET,',
'      X_DISPUTED        = :P45_X_DISPUTED,',
'      LAST_UPDATED      = TO_DATE(:P45_LAST_UPDATED,''DD-MON-YYYY''),',
'      COMMENTS          = :P45_COMMENTS,',
'      WORK_CODE         = :P45_WORK_CODE,',
'      MONTH_DATE        = :P45_MONTH_DATE,',
'      PROCESSOR_ID      = CLOSED,',
'      LOSS_ANALYST      = :P175_USERNAME,',
'      REVIEW_QUEUE      = NEXT_STEP',
'      WHERE DISPUTE_ID = :P45_DISPUTE_ID',
'       and   PROCESSOR_ID = 0;',
'     COMMIT;',
'',
'                  ',
'',
'        insert into LA_ASSIGN_HIST',
'        (',
'          CLIENT_CODE, ',
'          LAST_UPDATE,',
'          USER_ID,',
'          DISPUTE_ID',
'        )',
'        values ( NEXT_STEP,  SYSDATE, USERS_ID, :P45_DISPUTE_ID  );',
'        ',
'        COMMIT;',
'     ',
'END;',
''))
