,p_plug_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'select distinct CC.CLIENT_CODE, H.HIGH_QUEUE, L.LOW_QUEUE',
'FROM LA_CLIENT_CODES CC',
'left join ( select B.CLIENT_CODE, a.login AS HIGH_queue, A.USER_ID',
'              FROM LA_EMP_SECURITY A',
'              LEFT JOIN ( Select a.pid, v.CLIENT_CODE',
