'WHERE   DISPUTE_ID      =  :P249_DISPUTE_ID;',
'',
'COMMIT;',
'                  ',
'',
'        insert into LA_ASSIGN_HIST',
'        (',
'          CLIENT_CODE, ',
'          LAST_UPDATE,',
'          USER_ID,',
'          DISPUTE_ID',
'        )',
'        values ( NEXT_STEP,  SYSDATE, USERS_ID, :P249_DISPUTE_ID  );',
