,p_validation=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE ',
'',
'VALID_CODE NUMBER;',
'EQ_POS     NUMBER;',
'GT_POS     NUMBER;',
'LT_POS     NUMBER;',
'VALID_CHR  NUMBER;',
'',
'RESULTS    NUMBER;',
