'',
'STMT       VARCHAR2(1000 BYTE);',
'',
'',
'BEGIN',
'     ',
'VALID_CHR  := 0;',
'VALID_CODE := 0;',
'GT_POS     := 0;',
'LT_POS     := 0;',
'EQ_POS     := 0;',
'COMPARE    := ''50000.00'';',
'',
'',
'',
'',
'',
'VALID_CHR  := CASE WHEN INSTR(:P269_FIELD_VALUE,'','') > 0 THEN 0',
'                   WHEN INSTR(:P269_FIELD_VALUE,''$'') > 0 THEN 0',
'                   WHEN INSTR(:P269_FIELD_VALUE,''>'') > 0 THEN 1 ',
'                   WHEN INSTR(:P269_FIELD_VALUE,''<'') > 0 THEN 1',
'                   WHEN INSTR(:P269_FIELD_VALUE,''='') > 0 THEN 1  ',
'              ELSE 0',
'              END;  ',
'',
'    ',
'    EQ_POS := INSTR(:P269_FIELD_VALUE,''='');',
'    GT_POS := INSTR(:P269_FIELD_VALUE,''>'');',
'    LT_POS := INSTR(:P269_FIELD_VALUE,''<''); ',
'',
'',
'IF  (VALID_CHR = 1 ) ',
'   THEN        ',
'    VALID_CHR := CASE WHEN GT_POS = 0 AND LT_POS = 0 THEN 1     ',
'                      WHEN GT_POS > 0 AND LT_POS > 0 THEN 0 ',
'                      WHEN GT_POS > 0 AND EQ_POS = 0 THEN 1 ',
'                      WHEN LT_POS > 0 AND EQ_POS = 0 THEN 1                       ',
'                      WHEN GT_POS > 0 AND (EQ_POS - GT_POS) = 1 THEN 1',
'                      WHEN LT_POS > 0 AND (EQ_POS - LT_POS) = 1 THEN 1',
'                 ELSE 0',
'                 END;     ',
'',
'END IF;',
'',
'',
'IF (VALID_CHR = 1) ',
'   THEN ',
'      BEGIN',
'',
'',
'       STMT := ''select CASE WHEN 15000  ''||:P269_FIELD_VALUE||'' THEN 1 ELSE 1 END AS TEST from dual '';',
