'SAVED_WO         VARCHAR2(20 BYTE);',
'',
'BEGIN',
'',
' SELECT USER_ID ',
'   INTO USERS_ID ',
'   FROM LA_EMP_SECURITY ',
'   WHERE UPPER(LOGIN) = UPPER(:P175_USERNAME);',
'',
'    BEGIN',
'        SELECT WRITE_OFF ',
'        INTO SAVED_WO   ',
'        FROM LA_ASSIGN_DETAIL',
'        WHERE DISPUTE_ID = :P45_DISPUTE_ID',
