,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE ',
'',
'VALID_CODE NUMBER;',
'EQ_POS     NUMBER;',
'GT_POS     NUMBER;',
'LT_POS     NUMBER;',
'VALID_CHR  NUMBER;',
'',
'RESULTS    NUMBER;',
'COMPARE    NUMBER; ',
'',
'STMT       VARCHAR2(1000 BYTE);',
'',
'',
'BEGIN',
'     ',
'VALID_CHR  := 0;',
'VALID_CODE := 0;',
'GT_POS     := 0;',
'LT_POS     := 0;',
'EQ_POS     := 0;',
'COMPARE    := ''50000.00'';',
'',
'',
'',
'',
'',
'VALID_CHR  := CASE WHEN INSTR(:P269_FIELD_VALUE,'','') > 0 THEN 0',
