'                   WHEN INSTR(:P269_FIELD_VALUE,''>'') > 0 THEN 1 ',
'                   WHEN INSTR(:P269_FIELD_VALUE,''<'') > 0 THEN 1',
'                   WHEN INSTR(:P269_FIELD_VALUE,''='') > 0 THEN 1  ',
'              ELSE 0',
'              END;  ',
'',
'    ',
'    EQ_POS := INSTR(:P269_FIELD_VALUE,''='');',
'    GT_POS := INSTR(:P269_FIELD_VALUE,''>'');',
'    LT_POS := INSTR(:P269_FIELD_VALUE,''<''); ',
'',
'',
'IF  (VALID_CHR = 1 ) ',
'   THEN        ',
'    VALID_CHR := CASE WHEN GT_POS = 0 AND LT_POS = 0 THEN 1     ',
