'                      WHEN GT_POS > 0 AND EQ_POS = 0 THEN 1 ',
'                      WHEN LT_POS > 0 AND EQ_POS = 0 THEN 1                       ',
'                      WHEN GT_POS > 0 AND (EQ_POS - GT_POS) = 1 THEN 1',
