,p_process_sequence=>35
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Update Last Update Date / Completed / Saved / SQA Date'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'BEGIN',
'',
'',
'  IF ( :P222_FRAUD IS NULL  and :P222_QP IS NULL ) ',
'      THEN ',
'',
'         MERGE INTO SQA_TD_DATA Z',
'         USING (',
'           SELECT PID, LOANNUMBER, SPIPROPERTYID, ORDER_NUM, SYSDATE AS LAST_UPDATE_DT, ''Y'' AS COMPLETED, ''Y'' AS SAVED, CASE WHEN TOP_DOWN_DT IS NULL THEN SYSDATE WHEN TOP_DOWN_DT = '''' THEN SYSDATE ELSE TOP_DOWN_DT END AS TOP_DOWN_DT',
'           FROM SQA_TD_DATA',
'           WHERE PID = :P222_PID',
'         ) X ON (Z.PID = X.PID)',
'       WHEN MATCHED THEN',
'        UPDATE',
'          SET     Z.COMPLETED = X.COMPLETED, Z.SAVED = X.SAVED, Z.LAST_UPDATE_DT = X.LAST_UPDATE_DT, Z.TOP_DOWN_DT = X.TOP_DOWN_DT;',
'        COMMIT;',
' END IF;',
' ',
' IF ( :P222_FRAUD IS NOT NULL )  ',
'     THEN ',
'       UPDATE SQA_TD_DATA Z ',
'       SET  Z.COMPLETED = ''Y'', Z.SAVED = ''Y'', Z.LAST_UPDATE_DT = SYSDATE, Z.TOP_DOWN_DT = NVL(Z.TOP_DOWN_DT,SYSDATE), Z.FRAUD_INDC = ''Yes''',
'       WHERE EXISTS ( SELECT 1',
'                      FROM SQA_TD_DATA X',
'                      WHERE X.CONTRACTOR = :P222_CONTRACTOR',
'                      AND   X.COMPLETED  IS NULL ',
'                      AND   X.SAVED      IS NULL',
'                      AND   X.PID        = Z.PID); ',
'      COMMIT;',
'      ',
'      UPDATE SQA_VENDOR_LIST A SET FOLLOW_UP_SAT = ''Fraud/Fail'', FOLLOW_UP = ''Followup'' WHERE VENDOR_CODE = :P222_CONTRACTOR;',
'      COMMIT;',
'      ',
'  END IF;',
'  ',
'   IF (:P222_QP IS NOT NULL ) ',
'       THEN',
'       UPDATE SQA_TD_DATA Z ',
'       SET  Z.COMPLETED = ''Y'', Z.SAVED = ''Y'', Z.LAST_UPDATE_DT = SYSDATE, Z.TOP_DOWN_DT = NVL(Z.TOP_DOWN_DT,SYSDATE), Z.QP_INDC = ''Yes''',
'       WHERE EXISTS ( SELECT 1',
'                      FROM SQA_TD_DATA X',
'                      WHERE X.CONTRACTOR = :P222_CONTRACTOR',
'                      AND   X.COMPLETED  IS NULL ',
'                      AND   X.SAVED      IS NULL',
'                      AND   X.PID        = Z.PID); ',
'      COMMIT;',
'      ',
'      UPDATE SQA_VENDOR_LIST A SET A.FOLLOW_UP_SAT = ''QP'', A.FOLLOW_UP = ''Followup'',  A.STANDING = SQA_LOVS.max_standing (A.STANDING ) WHERE VENDOR_CODE = :P222_CONTRACTOR;',
'      COMMIT;',
'      ',
'  END IF;',
'  ',
'END;',
''))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(9578114972800691)
